<span style="color: #9cdcfe;">$request</span>-&gt;<span style="color: #dcdcaa;">input</span>(<span style="color: #ce9178;">'name'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$student</span>-&gt;<span style="color: #9cdcfe;">email</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$request</span>-&gt;<span style="color: #dcdcaa;">input</span>(<span style="color: #ce9178;">'email'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$student</span>-&gt;<span style="color: #9cdcfe;">course</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$request</span>-&gt;<span style="color: #dcdcaa;">input</span>(<span style="color: #ce9178;">'course'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$student</span>-&gt;<span style="color: #9cdcfe;">section</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$request</span>-&gt;<span style="color: #dcdcaa;">input</span>(<span style="color: #ce9178;">'section'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$student</span>-&gt;<span style="color: #dcdcaa;">save</span>();</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">return</span>&nbsp;<span style="color: #dcdcaa;">redirect</span>()-&gt;<span style="color: #dcdcaa;">back</span>()-&gt;<span style="color: #dcdcaa;">with</span>(<span style="color: #ce9178;">'status'</span>,<span style="color: #ce9178;">'Student&nbsp;Added&nbsp;Successfully'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>}</div></div>
