troller</span> <span style="color: #569cd6;">extends</span> <span style="color: #4ec9b0;">Controller</span></div><div><span>{</span></div><div>&nbsp; &nbsp; <span style="color: #569cd6;">public</span> <span style="color: #569cd6;">function</span> <span style="color: #dcdcaa;">store</span>(<span style="color: #4ec9b0;">Request</span> <span style="color: #9cdcfe;"><span>$</span>request</span>)</div><div>&nbsp; &nbsp; <span>{</span></div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;"><span>$</span>student</span> = <span style="color: #569cd6;">new</span> <span style="color: #4ec9b0;">Student</span>;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;"><span>$</span>student</span>-&gt;<span style="color: #9cdcfe;">name</span> = <span style="color: #9cdcfe;"><span>$</span>request</span>-&gt;<span style="color: #dcdcaa;">input</span>(<span style="color: #ce9178;">'name'</span>);</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #c586c0;">if</span>(<span style="color: #9cdcfe;"><span>$</span>request</span>-&gt;<span style="color: #dcdcaa;">hasfile</span>(<span style="color: #ce9178;">'image'</span>))</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span>{</span></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;"><span>$</span>file</span> = <span style="color: #9cdcfe;"><span>$</span>request</span>-&gt;<span style="color: #dcdcaa;">file</span>(<span style="color: #ce9178;">'image'</span>);</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;"><span>$</span>extenstion</span> = <span style="color: #9cdcfe;"><span>$</span>file</span>-&gt;<span style="color: #dcdcaa;">getClientOriginalExtension</span>();</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;"><span>$</span>filename</span> = <span style="color: #dcdcaa;">time</span>().<span style="color: #ce9178;">'.'</span>.<span style="color: #9cdcfe;"><span>$</span>extenstion</span>;</div><div>&nbsp