="color: #569cd6;">&lt;?php</span></div><div><span style="color: #dcdcaa;">session_start</span>();</div><div><span style="color: #c586c0;">include</span>(<span style="color: #ce9178;">'dbconfig.php'</span>);</div><br><div><span style="color: #c586c0;">require</span> <span style="color: #ce9178;">'vendor/autoload.php'</span>;</div><br><div><span style="color: #569cd6;">use</span> PhpOffice\PhpSpreadsheet\<span style="color: #4ec9b0;">Spreadsheet</span>;</div><div><span style="color: #569cd6;">use</span> PhpOffice\PhpSpreadsheet\Writer\<span style="color: #4ec9b0;">Xlsx</span>;</div><br><div><span style="color: #c586c0;">if</span>(<span style="color: #dcdcaa;">isset</span>(<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'save_excel_data'</span>]))</div><div>{</div><div>&nbsp; &nbsp; <span style="color: #9cdcfe;">$fileName</span> = <span style="color: #9cdcfe;">$_FILES</span>[<span style="color: #ce9178;">'import_file'</span>][<span style="color: #ce9178;">'name'</span>];</div><div>&nbsp; &nbsp; <span style="color: #9cdcfe;">$file_ext</span> = <span style="color: #dcdcaa;">pathinfo</span>(<span style="color: #9cdcfe;">$fileName</span>, PATHINFO_EXTENSION);</div><br><div>&nbsp; &nbsp; <span style="color: #9cdcfe;">$allowed_ext</span> = [<span style="color: #ce9178;">'xls'</span>,<span style="color: #ce9178;">'csv'</span>,<span style="color: #ce9178;">'xlsx'</span>];</div><br><div>&nbsp; &nbsp; <span style="color: #c586c0;">if</span>(<span style="color: #dcdcaa;">in_array</span>(<span style="color: #9cdcfe;">$file_ext</span>, <span style="color: #9cdcfe;">$allowed_ext</span>))</div><div>&nbsp; &nbsp; {</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;">$inputFileNamePath</span> = <span style="color: #9cdcfe;">$_FILES</span>[<span style="color: #ce9178;">'import_file'</span>][<span style="color: #ce9178;">'tmp_name'</span>];</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;">$spreadsheet</span> = \PhpOffice\PhpSp