                            <p><br></p><p><b>Step 2: </b>Create a page with the name code.php and then&nbsp;<span style="font-size: 1rem;">goto your phpmyadmin and create one more column in your register table named usertype (Varchar - 50)&nbsp;</span><span style="font-size: 1rem;">and paste the below code.</span></p><div class="alert funda-bg" style="color: rgb(212, 212, 212); background-color: rgb(30, 30, 30); font-family: Consolas, &quot;Courier New&quot;, monospace; font-size: 14px; line-height: 19px; white-space: pre;"><div><span style="color: #569cd6;">&lt;?php</span></div><div><span style="color: #dcdcaa;">include</span>(<span style="color: #ce9178;">'security.php'</span>);</div><br><div><span style="color: #c586c0;">if</span>(<span style="color: #dcdcaa;">isset</span>(<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'registerbtn'</span>]))</div><div>{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$username</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'username'</span>];</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$email</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'email'</span>];</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$password</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'password'</span>];</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$cpassword</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'confirmpassword'</span>];</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$usertype</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'usertype'</span>];</div><div>&nbsp;&nbsp;&nbsp;&nbsp;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$email_query</span>&nbsp;=&nbsp;<span style="color: #ce9178;">"</span><span style="color: #569cd6;">SELECT</span><span style="color: #ce9178;">&nbsp;</span>*<span style="color: #ce9178;">&nbsp;</span><span style="color: #569cd6;">FROM</span><span style="color: #ce9178;">&nbsp;register&nbsp;</span><span style="color: #569cd6;">WHERE</span><span style="color: #ce9178;">&nbsp;email</span>=<span style="color: #ce9178;">'</span><span style="color: #9cdcfe;">$email</span><span style="color: #ce9178;">'&nbsp;</span><span style="color: #ce9178;">"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$email_query_run</span>&nbsp;=&nbsp;<span style="color: #dcdcaa;">mysqli_query</span>(<span style="color: #9cdcfe;">$connection</span>,&nbsp;<span style="color: #9cdcfe;">$email_query</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">if</span>(<span style="color: #dcdcaa;">mysqli_num_rows</span>(<span style="color: #9cdcfe;">$email_query_run</span>)&nbsp;&gt;&nbsp;<span style="color: #b5cea8;">0</span>)</div><div>&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'status'</span>]&nbsp;=&nbsp;<span style="color: #ce9178;">"Email&nbsp;Already&nbsp;Taken.&nbsp;Please&nbsp;Try&nbsp;Another&nbsp;one."</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'status_code'</span>]&nbsp;=&nbsp;<span style="color: #ce9178;">"error"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #dcdcaa;">header</span>(<span style="color: #ce9178;">'Location:&nbsp;register.php'</span>);&nbsp;&nbsp;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">else</span></div><div>&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">if</span>(<span style="color: #9cdcfe;">$password</span>&nbsp;===&nbsp;<span style="color: #9cdcfe;">$cpassword</span>)</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$query</span>&nbsp;=&nbsp;<span style="color: #ce9178;">"</span><span style="color: #569cd6;">INSERT</span><span style="color: #ce9178;">&nbsp;</span><span style="color: #569cd6;">INTO</span><span style="color: #ce9178;">&nbsp;register&nbsp;(username,email,</span><span style="color: #569cd6;">password</span><span style="color: #ce9178;">,usertype)&nbsp;</span><span style="color: #569cd6;">VALUES</span><span style="color: #ce9178;">&nbsp;('</span><span style="color: #9cdcfe;">$username</span><span style="color: #ce9178;">','</span><span style="color: #9cdcfe;">$email</span><span style="color: #ce9178;">','</span><span style="color: #9cdcfe;">$password</span><span style="color: #ce9178;">','</span><span style="color: #9cdcfe;">$usertype</span><span style="color: #ce9178;">')</span><span style="color: #ce9178;">"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$query_run</span>&nbsp;=&nbsp;<span style="color: #dcdcaa;">mysqli_query</span>(<span style="color: #9cdcfe;">$connection</span>,&nbsp;<span style="color: #9cdcfe;">$query</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">if</span>(<span style="color: #9cdcfe;">$query_run</span>)</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #6a9955;">//&nbsp;echo&nbsp;"Saved";</span></div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'status'</span>]&nbsp;=&nbsp;<span style="color: #ce9178;">"Admin&nbsp;Profile&nbsp;Added"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'status_code'</span>]&nbsp;=&nbsp;<span style="color: #ce9178;">"success"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #dcdcaa;">header</span>(<span style="color: #ce9178;">'Location:&nbsp;register.php'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">else</span>&nbsp;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'status'</span>]&nbsp;=&nbsp;<span style="color: #ce9178;">"Admin&nbsp;Profile&nbsp;Not&nbsp;Added"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'status_code'</span>]&nbsp;=&nbsp;<span style="color: #ce9178;">"error"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #dcdcaa;">header</span>(<span style="color: #ce9178;">'Location:&nbsp;register.php'</span>);&nbsp;&nbsp;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">else</span>&nbsp;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&n