                            <p><b style="font-family: var(--bs-font-sans-serif); font-size: 1rem;">Step 4:</b><span style="font-family: var(--bs-font-sans-serif); font-size: 1rem;"> Get back to your code.php file and paste the below code:</span><br></p><div class="alert funda-bg" style="color: rgb(212, 212, 212); background-color: rgb(30, 30, 30); font-family: Consolas, &quot;Courier New&quot;, monospace; font-size: 14px; line-height: 19px; white-space: pre;"><div><span style="color: #569cd6;">&lt;?php</span></div><div><span style="color: #dcdcaa;">include</span>(<span style="color: #ce9178;">'security.php'</span>);</div><br><div><span style="color: #c586c0;">if</span>(<span style="color: #dcdcaa;">isset</span>(<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'login_btn'</span>]))</div><div>{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$email_login</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'emaill'</span>];&nbsp;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$password_login</span>&nbsp;=&nbsp;<span style="color: #9cdcfe;">$_POST</span>[<span style="color: #ce9178;">'passwordd'</span>];&nbsp;</div><br><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$query</span>&nbsp;=&nbsp;<span style="color: #ce9178;">"</span><span style="color: #569cd6;">SELECT</span><span style="color: #ce9178;">&nbsp;</span>*<span style="color: #ce9178;">&nbsp;</span><span style="color: #569cd6;">FROM</span><span style="color: #ce9178;">&nbsp;register&nbsp;</span><span style="color: #569cd6;">WHERE</span><span style="color: #ce9178;">&nbsp;email</span>=<span style="color: #ce9178;">'</span><span style="color: #9cdcfe;">$email_login</span><span style="color: #ce9178;">'&nbsp;</span><span style="color: #569cd6;">AND</span><span style="color: #ce9178;">&nbsp;</span><span style="color: #569cd6;">password</span>=<span style="color: #ce9178;">'</span><span style="color: #9cdcfe;">$password_login</span><span style="color: #ce9178;">'&nbsp;</span><span style="color: #569cd6;">LIMIT</span><span style="color: #ce9178;">&nbsp;</span><span style="color: #b5cea8;">1</span><span style="color: #ce9178;">"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$query_run</span>&nbsp;=&nbsp;<span style="color: #dcdcaa;">mysqli_query</span>(<span style="color: #9cdcfe;">$connection</span>,&nbsp;<span style="color: #9cdcfe;">$query</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$usertypes</span>&nbsp;=&nbsp;<span style="color: #dcdcaa;">mysqli_fetch_array</span>(<span style="color: #9cdcfe;">$query_run</span>);</div><br><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">if</span>(<span style="color: #9cdcfe;">$usertypes</span>[<span style="color: #ce9178;">'usertype'</span>]&nbsp;==&nbsp;<span style="color: #ce9178;">"admin"</span>)</div><div>&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'username'</span>]&nbsp;=&nbsp;<span style="color: #9cdcfe;">$email_login</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #dcdcaa;">header</span>(<span style="color: #ce9178;">'Location:&nbsp;index.php'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">else</span>&nbsp;<span style="color: #c586c0;">if</span>(<span style="color: #9cdcfe;">$usertypes</span>[<span style="color: #ce9178;">'usertype'</span>]&nbsp;==&nbsp;<span style="color: #ce9178;">"user"</span>)</div><div>&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'cusername'</span>]&nbsp;=&nbsp;<span style="color: #9cdcfe;">$email_login</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #dcdcaa;">header</span>(<span style="color: #ce9178;">'Location:&nbsp;../index.php'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">else</span></div><div>&nbsp;&nbsp;&nbsp;&nbsp;{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #9cdcfe;">$_SESSION</span>[<span style="color: #ce9178;">'status'</span>]&nbsp;=&nbsp;<span style="color: #ce9178;">"Email&nbsp;/&nbsp;Password&nbsp;is&nbsp;Invalid"</span>;</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #dcdcaa;">header</span>(<span style="color: #ce9178;">'Location:&nbsp;login.php'</span>);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;}</div><div>}</div><div><span style="color: #569cd6;">?</span><span style="color: #569cd6;">&gt;</span></div></div>
                            <p><b style="font-family: var(--bs-font-sans-serif); font-size: 1rem;">Step 5:</b><span style="font-family: var(--bs-font-sans-serif); font-size: 1rem;"> Create a folder called database and then create a file named dbconfig.php and paste the below code.</span><br></p><div class="alert funda-bg" style="color: rgb(212, 212, 212); background-color: rgb(30, 30, 30); font-family: Consolas, &quot;Courier New&quot;, monospace; font-size: 14px; line-height: 19px; white-space: pre;"><div><span style="color: #569cd6;">&lt;?php</span></div><br><div><span style="color: #9cdcfe;">$server_name</span>&nbsp;=&nbsp;<span style="color: #ce9178;">"localhost"</span>;</div><div><span style="color: #9cdcfe;">$db_username</span>&nbsp;=&nbsp;<span style="color: #ce9178;">"root"</span>;</div><div><span style="color: #9cdcfe;">$db_password</span>&nbsp;=&nbsp;<span style="color: #ce9178;">""</span>;</div><div><span style="color: #9cdcfe;">$db_name</span>&nbsp;=&nbsp;<span style="color: #ce9178;">"adminpanel"</span>;</div><br><div><span style="color: #9cdcfe;">$connection</span>&nbsp;=&nbsp;<span style="color: #dcdcaa;">mysqli_connect</span>(<span style="color: #9cdcfe;">$server_name</span>,<span style="color: #9cdcfe;">$db_username</span>,<span style="color: #9cdcfe;">$db_password</span>,<span style="color: #9cdcfe;">$db_name</span>);</div><br><div><span style="color: #c586c0;">if</span>(!<span style="color: #9cdcfe;">$connection</span>)</div><div>{</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #c586c0;">die</span>(<span style="color: #ce9178;">"Connection&nbsp;failed:&nbsp;"</span>&nbsp;.&nbsp;<span style="color: #dcdcaa;">mysqli_connect_error</span>());</div><div>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #dcdcaa;">echo</span>&nbsp;<span style="color: #ce9178;">'</span></div><div><span style="color: #ce9178;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div&nbsp;class="container"&gt;</span></div><div><span style="color: #ce9178;">&nbsp;&nbsp;&nbsp