 Consolas, &quot;Courier New&quot;, monospace; font-size: 14px; line-height: 19px; white-space: pre;"><div><span style="color: #569cd6;">&lt;?php</span></div><br><div><span style="color: #569cd6;">class</span> <span style="color: #4ec9b0;">StudentController</span></div><div>{</div><div>&nbsp; &nbsp; <span style="color: #569cd6;">public</span> <span style="color: #569cd6;">function</span> <span style="color: #dcdcaa;">__construct</span>()</div><div>&nbsp; &nbsp; {</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;">$db</span> = <span style="color: #569cd6;">new</span> <span style="color: #4ec9b0;">DatabaseConnection</span>;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #569cd6;">$this</span>-&gt;<span style="color: #9cdcfe;">conn</span> = <span style="color: #9cdcfe;">$db</span>-&gt;<span style="color: #9cdcfe;">conn</span>;</div><div>&nbsp; &nbsp; }</div><br><div>&nbsp; &nbsp; <span style="color: #569cd6;">public</span> <span style="color: #569cd6;">function</span> <span style="color: #dcdcaa;">index</span>()</div><div>&nbsp; &nbsp; {</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;">$studentQuery</span> = <span style="color: #ce9178;">"</span><span style="color: #569cd6;">SELECT</span><span style="color: #ce9178;"> </span>*<span style="color: #ce9178;"> </span><span style="color: #569cd6;">FROM</span><span style="color: #ce9178;"> students</span><span style="color: #ce9178;">"</span>;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #9cdcfe;">$result</span> = <span style="color: #dcdcaa;">mysqli_query</span>(<span style="color: #569cd6;">$this</span>-&gt;<span style="color: #9cdcfe;">conn</span>, <span style="color: #9cdcfe;">$studentQuery</span>);</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #c586c0;">if</span>(<span style="color: #9cdcfe;">$result</span>-&gt;<span style="color: #9cdcfe;">num_rows</span> &gt; <span style="color: #b5cea8;">0</span>){</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nb