item"</span><span style="color: #808080;">&gt;</span></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #808080;">&lt;</span><span style="color: #569cd6;">a</span> <span style="color: #9cdcfe;">class</span>=<span style="color: #ce9178;">"nav-link </span><span style="color: #569cd6;">&lt;?=</span> <span style="color: #9cdcfe;">$page</span> == <span style="color: #ce9178;">'about-us.php'</span> ? <span style="color: #ce9178;">'active'</span>:<span style="color: #ce9178;">''</span>; <span style="color: #569cd6;">?</span><span style="color: #569cd6;">&gt;</span><span style="color: #ce9178;">"</span> <span style="color: #9cdcfe;">href</span>=<span style="color: #ce9178;">"about-us.php"</span><span style="color: #808080;">&gt;</span>About Us<span style="color: #808080;">&lt;/</span><span style="color: #569cd6;">a</span><span style="color: #808080;">&gt;</span></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #808080;">&lt;/</span><span style="color: #569cd6;">li</span><span style="color: #808080;">&gt;</span></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #808080;">&lt;</span><span style="color: #569cd6;">li</span> <span style="color: #9cdcfe;">class</span>=<span style="color: #ce9178;">"nav-item"</span><span style="color: #808080;">&gt;</span></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color: #808080;">&lt;</span><span style="color: #569cd6;">a</span> <span style="color: #9cdcfe;">class</span>=<span style="color: #ce9178;">"nav-link </span><span style="color: #569cd6;">&lt;?=</span> <span style="color: #9cdcfe;">$page</span> == <span style="color: #ce9178;">'cart.php'</span> ? <span style="color: #ce9178;">'active'</span>:<span style="color: #ce9178;">''</span>; <span style="color: #569cd6;">?</span><span style="color: #569cd6;">&gt;</span><span style="color: #ce9178;">"</span> <span style="color: #9cdcfe;">href</span>=<span style="color: #ce9178;">"cart.php"</span><span style="color: #808080;"